mapper.insert:
INSERT
INTO TBL_PRODUCT A
    (
       A.PRODUCT_NO
     , A.PRODUCT_NAME
     , A.PRODUCT_PRICE
     , A.PRODUCT_DESC
     , A.PRODUCT_QTY
     , A.CATEGORY_CODE
)
VALUES
    (
    SEQ_PRODUCT_NO.NEXTVAL
        , #{ productName }
        , #{ productPrice }
        , #{ productDesc }
        , #{ productQty }
        , #{ categoryCode }

    )

INSERT
INTO TBL_PRODUCT_IMAGE A
    (
  A.FILE_NO
, A.ORIGIN_NAME
, A.STORED_NAME
, A.THUMBNAIL
, A.PRODUCT_NO
, A.IMG_PATH
)
VALUES
    (
    SEQ_FILE_NO.NEXTVAL
        , #{ originName }
        , #{ storedName }
        , #{ isThumbnail }
        , SEQ_PRODUCT_NO.CURRVAL
        , #{ imgPath }
   )

mapper.update:
UPDATE TBL_PRODUCT A
    SET
    A.PRODUCT_NAME = #{ productName }
  , A.PRODUCT_PRICE = #{  productPrice }
  , A.PRODUCT_DESC = #{  productDesc }
  , A.PRODUCT_QTY = #{  productQty }
  , A.CATEGORY_CODE = #{  categoryCode }
WHERE A.PRODUCT_NO = #{  productNo }

UPDATE TBL_PRODUCT_IMAGE A
SET

    A.ORIGIN_NAME = #{ originName }
  , A.STORED_NAME = #{ storedName }
  , A.THUMBNAIL = #{ isThumbnail }
  , A.IMG_PATH = #{ imgPath }

WHERE A.PRODUCT_NO = #{ productNo }
AND A.FILE_NO = #{ fileNo }

mapper.delete:
DELETE
FROM TBL_PRODUCT_IMAGE
WHERE PRODUCT_NO = #{ productNo }

DELETE
FROM TBL_PRODUCT
WHERE PRODUCT_NO = #{ productNo }
